import React, { useState } from 'react';

function App() {
  const [number, setNumber] = useState(0); 

  const incrementNumber = () => {
    setNumber(number + 1); 
  };

  return (
    <div className="App">
      <h1>Ứng dụng React với hai Component A và B</h1>
      <ComponentA number={number} incrementNumber={incrementNumber} />
      <ComponentB incrementNumber={incrementNumber} />
    </div>
  );
}

function ComponentA({ number, incrementNumber }) {
  return (
    <div>
      <h2>Component A</h2>
      <p>Number: {number}</p>
      <button onClick={incrementNumber}>Tăng số trong Component A</button>
    </div>
  );
}

function ComponentB({ incrementNumber }) {
  return (
    <div>
      <h2>Component B</h2>
      <button onClick={incrementNumber}>Tăng số trong Component A từ Component B</button>
    </div>
  );
}

export default App;
